#!/bin/bashecho "========================================"echo "CBR Theorie Examen - 300 Vragen"echo "========================================"echoecho "Dit start je CBR theorie examen training"echoecho "Wacht even terwijl de server start..."echo# Check if Node.js is installedif command -v node &> /dev/null; then    echo "Node.js gevonden ✓"else    echo "Node.js niet gevonden. Installeer Node.js van https://nodejs.org/"    exit 1fi# Check if npm is installedif command -v npm &> /dev/null; then    echo "npm gevonden ✓"else    echo "npm niet gevonden. Installeer npm."    exit 1fi# Install dependencies if node_modules doesn't existif [ ! -d "node_modules" ]; then    echo "Dependencies installeren..."    npm installfiecho "Server wordt gestart..."echoecho "Open je browser en ga naar: http://localhost:3000"echoecho "Druk op Ctrl+C om te stoppen"echonpm start